sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the current date and passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Get date objects - no timestamp
		var today = rightnow.getLocalDate();
		var ispast = testdate.getLocalDate();

		// Compare dates to see if passed in date is in the past.
// 		if(today.compareTo(ispast) == 1) {
// 			return true;
// 		} else {
// 			return false;
// 		}

		if(ispast.before(today)) {
			return true;
		} else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		// Create GlideDateTime objects for the current date and passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Get date objects - no timestamp
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		// Compare dates to see if they are the same
		if(today.compareTo(istoday) == 0) {
			return true;
		} else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
